/* Função responsável por processar os pedidos conforme regras de negócio.
Pedidos urgentes são sinalizados no payload (campo "urgente").
A ordenação não é aplicada para manter a ordem original de entrada, conforme comportamento apresentado no exemplo do desafio.
Caso necessário, a priorização pode ser implementada via ordenação ou separação de fluxos.*/

let pedidos = msg.payload.pedidos;
let pedidosProcessados = [];

for (let i = 0; i < pedidos.length; i++) {
    let pedido = pedidos[i];
    // Regra de inadimplência (prioridade máxima)
    if (pedido.cliente_inadimplente) {
        pedido.decisao = "BLOQUEADO";
    }
    // Regras baseadas no valor do pedido
    else if (pedido.valor < 1000) {
        pedido.decisao = "APROVADO AUTOMATICAMENTE";
    }
    else if (pedido.valor <= 10000){
        pedido.decisao = "ENVIAR PARA GERENTE";
    }
    else {
        pedido.decisao = "ENVIAR PARA DIRETORIA"
    }

    pedidosProcessados.push(pedido);
}

/* Exemplo de ordenação por urgência (não aplicada neste desafio):
    pedidosProcessados.sort((a, b) => a.urgente === b.urgente ? 0 : a.urgente ? -1 : 1); */

// Retorna a lista processada mantendo a ordem original
msg.payload = pedidosProcessados
return msg;

let resultado = "Processamento concluído:\n";

for (let i = 0; i < msg.payload.length; i++) {
    let pedido = msg.payload[i];
    let motivo = "";

    if (pedido.cliente_inadimplente) {
        motivo = "Cliente inadimplente";
    }
    else if (pedido.valor < 1000) {
        motivo = "Valor < 1000";
    }
    else if (pedido.valor <= 10000) {
        motivo = "Valor entre 1000 e 10000";
    }
    else {
        motivo = "Valor > 10000";
    }

    if (pedido.urgente) {
        motivo += " | Prioridade: URGENTE";
    }

resultado += `
        ---------------------------------
        Pedido ID: ${pedido.id}
        Valor: ${pedido.valor} | Inadimplente: ${pedido.cliente_inadimplente} | Urgente: ${pedido.urgente}
        Decisão: ${pedido.decisao}
        Motivo: ${motivo}

`;
}

msg.payload = resultado;
return msg;

